body[yahoo] {-webkit-text-size-adjust:none;}
.ReadMsgBody {width: 100%; background-color: #e3e3e3;}
.ExternalClass {width: 100%; background-color: #e3e3e3;}
body     {width: 100%; background-color: #e3e3e3; margin:0; padding:0; -webkit-font-smoothing: antialiased;}
html {width: 100%; }
table {border-collapse: collapse;}
.btn {width:100%; text-align: center!important;}
.wlbtn {width:280px; text-align: center!important;}
.farm-row{ font-family:Arial, sans-serif; font-size:12px; padding: 3px; color:#333333}
.top10-img-Width {width:290px!important; text-align:center} 
span.mainmessage {font-size:34px;text-decoration:none;font-family:Arial,sans-serif;line-height:1; color:#FF9900; font-weight:500}

@media only screen and (max-width: 680px)  {
    body[yahoo] .deviceWidth {width:100%!important}
    body[yahoo] td[class=&quot;col&quot;] a img {
        width: 100% !important;
        max-width: 60px !important;
        height: auto !important;
    }
    body[yahoo] td[class=&quot;col&quot;] {
        width: 30% !important;
        max-width: 60px !important;
    }
    body[yahoo] .farmLinks {
        width: auto !important;
        text-align: left !important;
    }
    body[yahoo] .farmLinks td {
        text-align: left !important;
    }
    body[yahoo] td[class=&quot;farmContainer&quot;] {
        width: 50% !important;
        text-align: left !important;
    }
}

@media only screen and (max-width: 599px)  {
body[yahoo] .logoTop {width:100%!important}
body[yahoo] .top10-img-Width {width:440px!important; text-align:center}
body[yahoo] .center {text-align: center!important;}
body[yahoo] .footerLinks {width:32%; margin-bottom:40px}
body[yahoo] .btn {width:100%; text-align: center!important;}
body[yahoo] .wlbtn {width:100%; text-align: center!important;}
body[yahoo] td[class=&quot;lcol&quot;], body[yahoo] td[class=&quot;rcol&quot;] {
    display: inline-block !important;
    display: inline-block;
    width: 100% !important;
    float: none !important;
    overflow: visible !important;
    max-height:none !important;
    padding:0px !important;
}
body[yahoo] td[class=&quot;lcol&quot;] img, body[yahoo] td[class=&quot;rcol&quot;] img {
    width: 100% !important;
}
body[yahoo] .farmLinks {
    min-width: 148px !important;
}
body[yahoo] .farmBottom {width:100%!important}
body[yahoo] .logoTop {width:100%!important}
}

@media only screen and (max-width: 479px) {
body[yahoo] .top10-img-Width {width:290px!important; text-align:center}
body[yahoo] .center {text-align: center!important;}
body[yahoo] .footerLinks { width:32%; margin-bottom:40px}
body[yahoo] .btn {width:100%; text-align: center!important;}
body[yahoo] .wlbtn {width:100%; text-align: center!important;}
body[yahoo] td[class=&quot;x-site&quot;] {display: none;}
}
